function s(t,r="error"){const e=document.getElementById("alert-modal"),n=document.getElementById("alert-message"),o=document.getElementById("alert-close-btn");if(!e||!n||!o){console.error("Elementos do modal de alerta nÃ£o encontrados."),alert(t);return}n.textContent=t,e.className=`fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 ${r==="error"?"text-red-400":"text-green-400"}`,e.classList.remove("hidden"),o.onclick=()=>{e.classList.add("hidden")},e.onclick=l=>{l.target===e&&e.classList.add("hidden")}}export{s};
